TRUNCATE table t_settle_fee_info;

insert into t_settle_fee_info( fee_time , logisitic_tax , logisitic_service , alipay_service , tmall , juhuasuan , logisitic_tax_usd , logisitic_service_usd , alipay_service_usd , tmall_usd , juhuasuan_usd) SELECT Settle_time , sum(logisitic_tax) , sum(logisitic_service) , SUM(alipay_service) , sum(tmall) , sum(juhuasuan) , sum(logisitic_tax_usd) , sum(logisitic_service_usd) , SUM(alipay_service_usd) , sum(tmall_usd) , sum(juhuasuan_usd) from( select SUBSTR(a.Settle_time FROM 1 FOR 10) as Settle_time , sum(a.Fee_rmb_amount) as logisitic_tax , 0 logisitic_service , 0 as alipay_service , 0 as tmall , 0 as juhuasuan , sum(a.Fee_amount) as logisitic_tax_usd , 0 logisitic_service_usd , 0 as alipay_service_usd , 0 as tmall_usd , 0 as juhuasuan_usd from load_settlefee_info a where a.Remark like '%进口关税%' group by SUBSTR(a.Settle_time FROM 1 FOR 10) UNION select SUBSTR(a.Settle_time FROM 1 FOR 10) , 0 , sum(a.Fee_rmb_amount) , 0 , 0 , 0 , 0 , sum(a.Fee_amount) , 0 , 0 , 0 from load_settlefee_info a where a.Remark like '%服务费用%' group by SUBSTR(a.Settle_time FROM 1 FOR 10) UNION select SUBSTR(a.Settle_time FROM 1 FOR 10) , 0 , 0 , sum(a.Fee_rmb_amount) , 0 , 0 , 0 , 0 , sum(a.Fee_amount) , 0 , 0 from load_settlefee_info a where a.Remark like '%Alipay service fee%' group by SUBSTR(a.Settle_time FROM 1 FOR 10) UNION select SUBSTR(a.Settle_time FROM 1 FOR 10) , 0 , 0 , 0 , sum(a.Fee_rmb_amount) , 0 , 0 , 0 , 0 , sum(a.Fee_amount) , 0 from load_settlefee_info a where a.Remark like '%Tmall Global Commission%' group by SUBSTR(a.Settle_time FROM 1 FOR 10) UNION select SUBSTR(a.Settle_time FROM 1 FOR 10) , 0 , 0 , 0 , 0 , sum(a.Fee_rmb_amount) , 0 , 0 , 0 , 0 , sum(a.Fee_amount) from load_settlefee_info a where a.Remark like '%Juhuasuan Overseas Commission%' group by SUBSTR(a.Settle_time FROM 1 FOR 10)) as t group by Settle_time;

